import './App.css';
import TextBox from "./TextBox";
import React, {useState, useEffect, useRef} from 'react';
import axios from 'axios';
import "react-awesome-button/dist/styles.css";

function Route() {
    const [startLat, setStartLat] = useState("");
    const [startLon, setStartLon] = useState("");
    const [destLat, setDestLat] = useState("");
    const [destLon, setDestLon] = useState("");
    const [startStreet1, setStartStreet1] = useState("");
    const [startStreet2, setStartStreet2] = useState("");
    const [destStreet1, setDestStreet1] = useState("");
    const [destStreet2, setDestStreet2] = useState("");
    const [route, setRoute] = useState([]);
    //const [ways, setWays] = useState([]);
    const CANVAS_SIDE_LENGTH = 450;
    const SCROLL_WAIT = 100;
    const CLICK_RANGE = 5;
    //TODO: will probably want to turn these into useState() calls eventually when zoom and scroll
    // are implemented
    const [mapWindow, setMapWindow] = useState([41.8320, -71.4070, 0.0113])
    const topLat = mapWindow[0];
    const leftLon = mapWindow[1];
    const latLonRange = mapWindow[2];
    // const [topLat, setTopLat] = useState(41.8320);
    // const [leftLon, setLeftLon] = useState(-71.4070);
    // const [latLonRange, setLatLonRange] = useState(0.0113);
    let mouseDownX;
    let mouseDownY;
    // We expect latitudes within the map range to be LESS than the topLat
    // We expect longitudes within the map range to be GREATER than the leftLon
    // In order to get bottomLat or rightLon, just subtract latLonRange from topLat, or add it to leftLon

    const TILE_WIDTH = 0.0016; // this is an arbitrarily chosen-value, but it works well
    let topmostTileLat = useRef(topLat);
    let leftmostTileLon = useRef(leftLon);
    let bottommostTileLat = useRef(topmostTileLat.current);
    let rightmostTileLon = useRef(leftmostTileLon.current);
    let tilesLoadedRef = useRef([]);
    // Tiles should be JavaScript objects with properties topLat, leftLon, and an [] of ways
    // Ways should be length-4 list of Doubles: [lat1, lon1, lat2, lon2]

    const canvasRef = useRef();
    let ctxRef = useRef();
    let reloadCounter = useRef(0);

    /**
     * Code to run when the page loads
     */
    useEffect(() => {
        //console.log("page loaded!");
        const canvas = canvasRef.current;
        canvas.width = CANVAS_SIDE_LENGTH;
        canvas.height = CANVAS_SIDE_LENGTH;
        ctxRef.current = canvas.getContext("2d");
        // let ctx = ctxRef.current;
        // ctx.fillStyle = "green";
        // ctx.fillRect(0, 0, 100, 100);

        // Loading starting tiles is taken care of by the automatic redrawMap() call
        //const boundaries = getMapBoundaries();
        //loadTilesWithin(boundaries[0], boundaries[1], boundaries[2], boundaries[3]);
    }, [ ]);

    useEffect(() => {
        //TODO if we uncomment the below console.log, we'll see that the map gets reloaded four times every
        // time the canvas is clicked (once for each useState() parameter that gets changed)
        // which is kinda wasteful of resources, though it doesn't impact user experience
        // If we want to fix it, we could combine all four startLat/startLon/destLat/destLon
        // into one const [routeEndCoords, setRouteEndCoords] = useState(["","","",""])
        //console.log("reload number " + reloadCounter.current);
        redrawMap();
    })

    async function loadTilesWithin(topLatLimit, leftLonLimit, bottomLatLimit, rightLonLimit, reloadNum) {
        // We expect latitudes within the map range to be LESS than the topLat
        // We expect longitudes within the map range to be GREATER than the leftLon
        // const tilesLoadedTemp = tilesLoadedRef.current;
        let newTile;
        let curLat = topLatLimit;
        let curLon = leftLonLimit;
        while (curLon < rightLonLimit) {
            while (curLat > bottomLatLimit) {
                if (reloadNum !== reloadCounter.current) {
                    return;
                }
                // Check if the tile has already been loaded
                //console.log(tileLoaded(curLat, curLon, tilesLoadedRef.current));
                if (!tileLoaded(curLat, curLon, tilesLoadedRef.current)) {
                    const ways = await requestWays(getBoundingBox(curLat, curLon, TILE_WIDTH));
                    //console.log(ways);
                    newTile = {"tileLat": curLat, "tileLon": curLon, "ways": ways};
                    // Tiles previously had a randomly-generated "name" property: "name":Math.round(Math.random()*100)
                    // console.log("rendering tile for reload number " + reloadNum);
                    drawTile(newTile);

                    // console.log(newTile);
                    tilesLoadedRef.current.push(newTile);
                }

                curLat -= TILE_WIDTH;
            }
            // console.log("curLat: " + curLat);
            if (curLat < bottommostTileLat.current) {
                bottommostTileLat.current = curLat;
            }
            curLat = topLatLimit;
            curLon += TILE_WIDTH;
        }
        if (curLon > rightmostTileLon.current) {
            rightmostTileLon.current = curLon;
        }
        // console.log("num tiles loaded: " + tilesLoadedRef.current.length);
        //tilesLoadedRef.current = tilesLoadedTemp;
    }

    /**
     * Determines whether a tile with a given lat/lon coordinate has been cached on the client-side
     * or note. Returns a boolean.
     * @param lat - latitude coordinate of tile to check
     * @param lon - longitude coordinate of tile to check
     * @param loadedTiles - list of tiles that have been loaded already
     * @returns {boolean} - whether or not the tile is cached already
     */
    function tileLoaded(lat, lon, loadedTiles) {
        // console.log("checking tile " + lat + " " + lon);
        //console.log("num tiles checking: " + loadedTiles.length);
        let loaded = false;
        let checkTile;
        for (let i = 0; i < loadedTiles.length; i++) {
            checkTile = loadedTiles[i];
            loaded = loaded || (checkTile["tileLat"] === lat && checkTile["tileLon"] === lon);
        }
        return loaded;
    }

    /**
     * Makes an axios request.
     */
    async function requestRoute() {
        const toSend = {
            srclat: startLat,
            srclong: startLon,
            destlat: destLat,
            destlong: destLon
        };
        // console.log(toSend);

        let config = {
            headers: {
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*',
            }
        }
        const res = await axios.post(
            "http://localhost:4567/route",
            toSend,
            config
        );
        // console.log(res["data"]["route"]);
        return res["data"]["route"];
    }

    //uses post request to get ways within bounding box
    async function requestWays(coordinates) {
        const toSend = {
            lat1: coordinates[0],
            long1: coordinates[1],
            lat2: coordinates[2],
            long2: coordinates[3]
        };
        let config = {
            headers: {
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*',
            }
        }
        const res = await axios.post(
            "http://localhost:4567/ways",
            toSend,
            config
        );
        return res["data"]["ways"];
    }

    /**
     * Makes an axios request to get the coordinates of the node nearest to a clicked lat and lon.
     */
    function requestNearest(lat, lon, latSetter, lonSetter) {
        const toSend = {
            lat: lat,
            lon: lon
        };

        let config = {
            headers: {
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*',
            }
        }

        axios.post(
            "http://localhost:4567/nearest",
            toSend,
            config
        )
            .then(response => {
                // console.log(response.data);
                return response.data;
            })
            .then((data) => {
                const newLat = data["nearestLat"];
                const newLon = data["nearestLon"];
                return [newLat, newLon];
            })
            .then((newCoord) => {
                latSetter(newCoord[0]);
                lonSetter(newCoord[1]);
                setRoute([]);
                // redrawMap();
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    function clearLoadTiles() {
        const tilesLoadedTemp = tilesLoadedRef.current;
        // First, purge old tiles
        const mapBoundaries = getMapBoundaries();
        //these are backwards
        const bottomLat = mapBoundaries[2];
        const rightLon = mapBoundaries[3];
        let saveTiles = [];
        // Iterate over all the loaded tiles and see if they're still useful
        for (let i = 0; i < tilesLoadedTemp.length; i++) {
            const checkTile = tilesLoadedTemp[i];
            const tileBoundaries = getBoundingBox(checkTile["tileLat"], checkTile["tileLon"], TILE_WIDTH);
            // getBoundingBox() returns [lat1, lon1, lat2, lon2]
            const tileTopLat = tileBoundaries[0];
            const tileLeftLon = tileBoundaries[1];
            const tileBottomLat = tileBoundaries[2];
            const tileRightLon = tileBoundaries[3];
            // We expect latitudes within the map range to be LESS than the topLat
            // We expect longitudes within the map range to be GREATER than the leftLon
            const deleteTile = (tileBottomLat > topLat) || (tileTopLat < bottomLat)
                || (tileRightLon < leftLon) || (tileLeftLon > rightLon);
            // if (tileBottomLat > topLat) {
            //     console.log(checkTile["name"] + " tile is offscreen on the bottom side");
            // }
            // if (tileLeftLon > rightLon) {
            //     console.log(checkTile["name"] + " tile is offscreen on the right side");
            // }
            // if (deleteTile) {
            //     console.log("deleting tile");
            // }
            if (!deleteTile) {
                // console.log("rendering tile for reload number " + reloadNum);
                drawTile(tilesLoadedTemp[i]);
                saveTiles.push(checkTile);
            }
        }
        tilesLoadedRef.current = saveTiles;

        // update the loaded tile boundaries (how far in each direction have tiles been loaded?)
        // getBoundingBox() returns [lat1, lon1, lat2, lon2]
        while (getBoundingBox(topmostTileLat.current, leftmostTileLon.current, TILE_WIDTH)[2] > topLat){
            // console.log("adjusting loaded top boundary");
            topmostTileLat.current -= TILE_WIDTH;
        }
        while (getBoundingBox(topmostTileLat.current, leftmostTileLon.current, TILE_WIDTH)[3] < leftLon){
            // console.log("adjusting loaded left boundary");
            leftmostTileLon.current += TILE_WIDTH;
        }
        while (bottommostTileLat.current < bottomLat){
            // console.log("adjusting loaded bottom boundary");
            bottommostTileLat.current += TILE_WIDTH;
        }
        while (rightmostTileLon.current > rightLon){
            // console.log("adjusting loaded right boundary");
            rightmostTileLon.current -= TILE_WIDTH;
        }

        // Now, load new tiles
        while (leftmostTileLon.current > leftLon) {
            //console.log("gap at left");
            // loadTilesWithin(topmostTileLat.current, leftmostTileLon.current - TILE_WIDTH, bottommostTileLat.current, leftmostTileLon.current);
            leftmostTileLon.current -= TILE_WIDTH;
        }
        while (topmostTileLat.current < topLat) {
            //console.log("gap at top");
            // loadTilesWithin(topmostTileLat.current + TILE_WIDTH, leftmostTileLon.current, topmostTileLat.current, rightmostTileLon.current);
            topmostTileLat.current += TILE_WIDTH;
        }
        while (bottommostTileLat.current >= bottomLat) {
            //console.log("gap at bottom");
            /*loadTilesWithin(bottommostTileLat.current, leftmostTileLon.current - TILE_WIDTH,
                bottommostTileLat.current - (2.0 * TILE_WIDTH), rightmostTileLon.current);*/
            bottommostTileLat.current -= TILE_WIDTH;
        }
        while (rightmostTileLon.current <= rightLon) {
            //console.log("gap at right");
            /*loadTilesWithin(topmostTileLat.current, rightmostTileLon.current + TILE_WIDTH,
                bottommostTileLat.current, rightmostTileLon.current + (2.0 * TILE_WIDTH));*/
            rightmostTileLon.current += TILE_WIDTH;
        }
        loadTilesWithin(topmostTileLat.current, leftmostTileLon.current,
            bottommostTileLat.current, rightmostTileLon.current, reloadCounter.current);
        // console.log("num tiles after loading: " + tilesLoadedTemp.length);
        //drawLoadedRect();
    }

    function redrawMap() {
        clearCanvas();
        clearLoadTiles();
        drawStartEndCircles();
        drawRoute();
    }

    /**
     * Code to turn lat/lon coordinates into pixel coordinates for the canvas:
     * takes in a latitude and longitude coordinate, returns a length-2 list with the
     * corresponding pixel position on the canvas [left, top]
     * @param lat - the latitude coordinate to turn into a pixel left coordinate
     * @param lon - the longitude coordinate to turn into a pixel top coordinate
     * @returns {number[]} the corresponding pixel position on the canvas [left, top]
     */
    function latLonCoordToPix(lat, lon) {
        const normalizedLat = topLat - lat;
        const normalizedLon = lon - leftLon;
        // Because of weird latitude/longitude rules in the specific hemisphere Rhode Island is in:
        // We expect latitudes within the map range to be LESS than the topLat
        // We expect longitudes within the map range to be GREATER than the leftLon

        // REMEMBER: lat is y-value, lon is x-value!
        return [pixIfy(normalizedLon), pixIfy(normalizedLat)];
    }

    /**
     * Converts a single latitude/longitude coordinate value into a pixel value.
     * @param latLonVal the lat/lon coordinate to convert to pixel coordinates.
     * @returns {number} the pixel conversion of the lat/lon value
     */
    function pixIfy(latLonVal) {
        const pixOverLatLonFactor = CANVAS_SIDE_LENGTH / latLonRange;
        return latLonVal * pixOverLatLonFactor;
    }

    /**
     * Code takes in a pageX (NOT canvas x) and pageY and turns it into a latitude
     * and longitude coordinate.
     * Returns a length-2 list with [lat, lon].
     * @param left - the pixel left coordinate to turn into map latitude
     * @param top - the pixel top coordinate to turn into map longitude
     * @returns {number[]} the corresponding lat/lon coordinate on the canvas [lat, lon]
     */
    function pixCoordToLatLon(left, top) {
        const canvas = canvasRef.current;
        const normalizedLeft = left - canvas.offsetLeft;
        const normalizedTop = top - canvas.offsetTop;
        // console.log(normalizedLeft + ", " + normalizedTop);
        // console.log(normalizedLeft/CANVAS_SIDE_LENGTH + ", " + normalizedTop/CANVAS_SIDE_LENGTH);

        const returnLat = topLat - latLonIfy(normalizedTop);
        const returnLon = leftLon + latLonIfy(normalizedLeft);
        // console.log(returnLat, ", ", returnLon)
        // console.log(latLonCoordToPix(returnLat, returnLon));

        // REMEMBER: lat is y-value, lon is x-value!
        return [returnLat, returnLon];
    }

    /**
     * Turns a single value in pixels into a value in latitude/longitude coordinates.
     * @param pixVal the pixel value to convert to latitude/longitude coordinates.
     * @returns {number} the lat/lon conversion of the pixel value
     */
    function latLonIfy(pixVal) {
        const latLonOverPixFactor =  latLonRange / CANVAS_SIDE_LENGTH;
        return pixVal * latLonOverPixFactor;
    }

    /**
     * Get the lat/lon boundaries of the current map. May be helpful for the ways command.
     * Returns a length-4 list of [lat1, lon1, lat2, lon2].
     * @returns {(*|number)[]} a length-4 list of [lat1, lon1, lat2, lon2], representing the latitude
     * and longitude coordinates of the northwest and southeast corners of the current map frame.
     */
    function getMapBoundaries() {
        return getBoundingBox(topLat, leftLon, latLonRange);
    }

    /**
     * Get the lat/lon boundaries of a passed square area with unknown side length.
     * @param boxtopLat latitude coordinate of the northwest corner
     * @param boxleftLon longitude coordinate of the northwest corner
     * @param boxRange side length, in terms of lat/lon coordinates
     * @returns {(*|number)[]} a length-4 list of [lat1, lon1, lat2, lon2], representing the latitude
     * and longitude coordinates of the northwest and southeast corners of a passed square.
     */
    function getBoundingBox(boxtopLat, boxleftLon, boxRange) {
        // REMEMBER: lat is y-value, lon is x-value!
        return [boxtopLat, boxleftLon, boxtopLat - boxRange, boxleftLon + boxRange];
    }

    function handleClickOrDrag(mouseUpX, mouseUpY) {
        const xChange = mouseUpX - mouseDownX; // positive if the map is dragged to the right
        const yChange = mouseUpY - mouseDownY; // positive if the map is dragged downwards
        // We expect latitudes within the map range to be LESS than the topLat
        // We expect longitudes within the map range to be GREATER than the leftLon

        if (Math.abs(xChange) < CLICK_RANGE && Math.abs(yChange) < CLICK_RANGE) {
            // This was a click!
            const ctx = ctxRef.current;
            ctx.fillStyle = "green";
            // console.log(pixCoordToLatLon(mouseUpX, mouseUpY));
            const latLonCoord = pixCoordToLatLon(mouseUpX, mouseUpY);
            //if (startLat === 0 || startLat.toString() === "")
            if (startLat === "") {
                requestNearest(latLonCoord[0], latLonCoord[1], setStartLat, setStartLon);
            } else {
                requestNearest(latLonCoord[0], latLonCoord[1], setDestLat, setDestLon);
            }
        } else {
            reloadCounter.current += 1;
            // console.log("reload number " + reloadCounter.current);
            // This was a drag!
            // REMEMBER: lat is y-value, lon is x-value!
            //console.log(xChange, yChange);
            //console.log(latLonIfy(xChange), latLonIfy(yChange));
            //console.log(topLat + ", " + leftLon);
            //setLeftLon(leftLon - latLonIfy(xChange));
            const newLeftLon = leftLon - latLonIfy(xChange);
            /*
             * positive values, dragging the map to the right
             * moves the previous leftLon rightwards
             * meaning that the new leftLon should be less than the previous leftLon
             */
            //setTopLat(topLat + latLonIfy(yChange));
            const newTopLat = topLat + latLonIfy(yChange);
            /*
             * positive values, dragging the map downwards
             * moves the previous topLat downwards
             * meaning that the new topLat should be greater than the previous topLat
             */
            //console.log(topLat + ", " + leftLon);
            // redrawMap();
            const newMapWindow = [newTopLat, newLeftLon, mapWindow[2]];
            setMapWindow(newMapWindow);
        }
    }

    function handleZoomInOut(zoomedOut, mouseX, mouseY, numScrolls) {
        reloadCounter.current += 1;
        // console.log("reload number " + reloadCounter.current);
        const mouseLatLonCoord = pixCoordToLatLon(mouseX, mouseY);
        const zoomLatNormalized = topLat - mouseLatLonCoord[0];
        const zoomLonNormalized = mouseLatLonCoord[1] - leftLon;
        const latFraction = (zoomLatNormalized / latLonRange);
        const lonFraction = (zoomLonNormalized / latLonRange);

        // We expect latitudes within the map range to be LESS than the topLat
        // We expect longitudes within the map range to be GREATER than the leftLon
        if (zoomedOut) {
            const stepSize = (latLonRange * Math.pow(1.05, numScrolls)) - latLonRange;
            //setLatLonRange(latLonRange + stepSize);
            const newLatLonRange = latLonRange + stepSize;
            // We're zooming out, so the topLat should move upwards
            //setTopLat(topLat + (latFraction * stepSize));
            const newTopLat = topLat + (latFraction * stepSize);
            // and leftLon should move leftwards
            //setLeftLon(leftLon - (lonFraction * stepSize));
            const newLeftLon = leftLon - (lonFraction * stepSize);
            const newMapWindow = [newTopLat, newLeftLon, newLatLonRange];
            setMapWindow(newMapWindow);
        } else {
            const stepSize = latLonRange - (latLonRange * Math.pow(0.95, numScrolls));
            //setLatLonRange(latLonRange - stepSize);
            const newLatLonRange = latLonRange - stepSize;
            // We're zooming in, so the topLat should move downwards
            //setTopLat(topLat - (latFraction * stepSize));
            const newTopLat = topLat - (latFraction * stepSize);
            // and leftLon should move inwards
            //setLeftLon(leftLon + (lonFraction * stepSize));
            const newLeftLon = leftLon + (lonFraction * stepSize);
            const newMapWindow = [newTopLat, newLeftLon, newLatLonRange];
            setMapWindow(newMapWindow);
        }
        // redrawMap();
    }

