    function drawCircle(lat, lon, radius) {
        const ctx = ctxRef.current;
        const pixCoord = latLonCoordToPix(lat, lon);
        ctx.strokeStyle = "red";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(pixCoord[0], pixCoord[1], radius, 0, 2 * Math.PI);
        ctx.stroke();
    }

