            drawCircle(startLat, startLon, radius);
        }
        if (!(parseFloat(destLon) === 0 || destLon.toString() === ""
            || parseFloat(destLat) === 0 || destLat.toString() === "")){
            // drawing destination circle
            drawCircle(destLat, destLon, radius);
        }
    }

