    function traceRoute(way) {
        const ctx = ctxRef.current;
        let startLatCoord = way["startLat"]
        let startLonCoord = way["startLong"]
        let pixStart = latLonCoordToPix(startLatCoord, startLonCoord)
        ctx.fillStyle = "blue";
        ctx.strokeStyle = "blue";
        ctx.lineWidth = 4;
        ctx.beginPath();
        ctx.moveTo(pixStart[0], pixStart[1])
        let endLatCoord = way["destLat"]
        //console.log(endLatCoord)
        let endLongCoord = way["destLong"]
        //console.log(endLongCoord)
        let pixEnd = latLonCoordToPix(endLatCoord, endLongCoord)
        //console.log(pixEnd)
        ctx.lineTo(pixEnd[0], pixEnd[1])
        ctx.stroke();
    }

    //M picked colors randomly feel free to change colors if you would like
    function traceWay(way) {
