    //TODO we could probably lump all this functionality directly into the requestRoute() method
    // Since all we really need is to use the setRoute() method to store the route data
    async function getRoute() {
        // console.log("should draw the route");
        const routeTemp = await requestRoute();
        // console.log(routeTemp);
        setRoute(routeTemp);
        // drawRoute()

        // Now we'll auto-zoom to a proper display size!
        // We expect latitudes within the map range to be LESS than the topLat
        // We expect longitudes within the map range to be GREATER than the leftLon
        const latRange = Math.abs(startLat - destLat);
        const lonRange = Math.abs(startLon - destLon);
        const routeTopLat = Math.max(startLat, destLat);
        const routeLeftLon = Math.min(startLon, destLon);
        const maxRange = Math.max(latRange, lonRange);
        const centerLatPadding = (maxRange - latRange) / 2.0;
        const centerLonPadding = (maxRange - lonRange) / 2.0;
        const overallPadding = 0.1 * maxRange; // We'll add a border of 10% of the longest dimension of the path
        const newTopLat = routeTopLat + centerLatPadding + overallPadding;
        const newLeftLon = routeLeftLon - centerLonPadding - overallPadding;
        const newLatLonRange = maxRange + (2 * overallPadding);
        const newMapWindow = [newTopLat, newLeftLon, newLatLonRange];
        setMapWindow(newMapWindow);
    }

    function drawRoute() {
        for (const way of route) {
            traceRoute(way)
        }
